mapper.insert:
insert into files (
file_name, file_path, file_uuid, file_regdate
, child_key
, kinder_key
, user_key
)
values (
#{fileName}, #{filePath}, #{fileUuid}, NOW()
, #{childKey}
, #{kinderKey}
, #{userKey}
)

mapper.update:
update files
set file_name = #{fileName},
    file_path=#{filePath},
    file_uuid=#{fileUuid},
    file_regdate=NOW()
where 1 = 1

        and user_key = #{userKey}


        and kinder_key = #{kinderKey}


        and child_key = #{childKey}

mapper.select:
SELECT COUNT(*) FROM FILES
WHERE 1=1

  and user_key = #{userKey}


  and kinder_key = #{kinderKey}


    and child_key = #{childKey}
