mapper.select:
select * from USER
where user_type='U'

    and user_phone=#{userPhone}


    and user_name =#{userName}


    and user_Id =#{userId}


    and user_pw =#{userPw}

mapper.update:
update USER
set user_pw=#{userPw}

    ,user_birth =#{userBirth}


    ,user_postcode =#{userPostcode}


    ,user_address =#{userAddress}


    ,user_address_detail =#{userAddressDetail}

where user_id=#{userId}
